//* Controlar los menus con el hash
document.addEventListener("DOMContentLoaded", () => {
  if (window.location.hash) {
    const hash = window.location.hash.substring(1);
    switch (hash) {
      case "lista-categorias":
        borrarHash(".lista-categorias");
        break;
      case "lista-empleados":
        borrarHash(".lista-empleados");
        break;
      case "lista-proveedores":
        borrarHash(".lista-proveedores");
        break;
      case "producto-almacen":
        borrarHash(".producto-almacen");
        break;
      case "producto-minimo":
        borrarHash(".producto-minimo");
        break;
      case "producto-caducado":
        borrarHash(".producto-caducado");
        break;
    }
  }
});

function borrarHash(el) {
  const menu = document.querySelector(el);
  menu.click();
  window.history.pushState("", document.title, window.location.pathname);
}
